mapper.select:
SELECT COMMENT_ID, COMMENT_CONTENT, COMMENT_REGISTER_DATE, COMMENT_UPDATE_DATE, COMMENT_LIKES, BOARD_ID, USER_ID
FROM TBL_COMMENT
WHERE COMMENT_ID = #{commentId}

mapper.update:
UPDATE TBL_COMMENT
SET COMMENT_CONTENT = #{commentContent}, COMMENT_UPDATE_DATE = SYSDATE
WHERE COMMENT_ID = #{commentId}
